            mHeaderViewInfos.remove(i);
            mAreAllFixedViewsSelectable =
                    areAllListInfosSelectable(mHeaderViewInfos)
                    && areAllListInfosSelectable(mFooterViewInfos);
            notifyDataSetChanged();
            return true;
        }
    }
    return false;
}
```
