``` Javascript
// a native alert message with a browser safe fallback
window.ngAlert = function (message, callback, title, button) {
    if(typeof callback === 'undefined')callback = null;
    title = (title != undefined) ? title : 'Alert';
    button = (button != undefined) ? button : 'Ok';

    if (navigator.notification) {
        navigator.notification.alert(
            message, // message
            callback, //callback method... 
            title, // title
            button // buttonName
        );
    } else {
        alert(message);
        if(typeof callback === "function") callback();
    }
};

// a native confirm message with a browser safe fallback
window.ngConfirm = function (message, callback, title) {
    title = (title != undefined) ? title : 'Confirm';

    if (navigator.notification) {
        function callbacker(buttonIndex){
            var val = (buttonIndex == 1);
            callback(val);
        }
        navigator.notification.confirm(
            message, // message
            callbacker, //callback method...
            title // title
        );
    } else {
        var r = confirm(title);
        callback(r);
    }
};
```
