(function() {
    'use strict';

    angular
        .module('factories')
        .factory('DAOFactory', DAOFactory);

    DAOFactory.$inject = ['$cordovaSQLite', '$q', '$ionicPlatform', 'localStorageService'];

    function DAOFactory($cordovaSQLite, $q, $ionicPlatform, localStorageService) {
        var self = this;

        self.db = null;

        self.init = function() {
            $ionicPlatform.ready(function() {
                self.db = $cordovaSQLite.openDB("upwayCrm.db");


                $cordovaSQLite.execute(self.db, "CREATE TABLE IF NOT EXISTS user (id integer primary key, username text, name text, fk_usergroup integer, fk_empresa integer, company text)");

                console.info(self.db);
            });

        }

        self.createTable = function(table, tableColumns) {
            var columns = [];
            for (var column in tableColumns)
                columns.push(column + " " + tableColumns[column]);

            var sql = "CREATE TABLE IF NOT EXISTS " + table + " (" + columns.join() + ")";

            console.group();
            console.info("Tabela criada: %s", table);

            console.groupEnd();
            return $cordovaSQLite.execute(self.db, sql);
        }

        self.query = function(query, parameters) {
            parameters = parameters || [];
            var q = $q.defer();
            $ionicPlatform.ready(function() {

                $cordovaSQLite.execute(self.db, query, parameters)
                    .then(function(result) {
                        q.resolve(result);
                    }, function(error) {
                        console.warn('Erro no acesso ao banco de dados!');
                        console.warn(error);
                        console.info(arguments);
                        q.reject(error);
                    });
            });

            return q.promise;
        }

        self.delete = function(table, id) {
            if (id)
                return self.query("UPDATE ? SET ACTIVE = (?), UPDATEDBY = (?), UPDATEDON = (?) WHERE ID = ?", [
                    table, 0, localStorageService.get('id'), Date(), id
                ]);

            console.error('Id value is null: %s | %s', table, id);
        }

        self.getAll = function(result) {
            var output = [];

            for (var i = 0, count = result.rows.length; i < count; i++)
                output.push(result.rows.item(i));

            return output;
        }

        self.getById = function(result) {
            return result.rows.item(0);
        }

        self.buildSQLColumns = function(objTableColumns) {
            return Object.keys(objTableColumns).join();
        }

        self.buildValueColumns = function(tableColumns, objValueColumns) {
            var value = [];

            for (var column in tableColumns)
                value.push(objValueColumns[column]);

            return value;
        }

        self.buildSQLColumnsUpdate = function(objTableColumns) {
            var columns = [];
            for (var column in objTableColumns)
                columns.push(column + " = (?)");

            return columns.join();
        }

        self.buildSQLColumnsValueInsert = function(objTableColumns) {
            var value = [];

            for (var column in objTableColumns)
                value.push("?");

            return value.join();
        }

        return self;
    }
})();
```
