public Function toElementKey(final Schema schema) {
        return element -> {
            final Element key = element.emptyClone();
            final SchemaElementDefinition elDef = schema.getElement(key.getGroup());
            if (elDef == null) {
                throw new RuntimeException("received element " + element + " which belongs to a " +
                        "group not found in the schema");
            }
        final Set<String> groupBy = elDef.getGroupBy();
        for (final String property : groupBy) {
            key.putProperty(property, element.getProperty(property));
        }
        return key;
    };
}

