/// 
        /// 使用Post方法获取字符串结果
        /// 
        /// 
        /// 
        /// 
        /// 需要上传的文件，Key：对应要上传的Name，Value：本地文件名
        /// 
        /// 证书，如果不需要则保留null
        /// 
        /// 验证服务器证书回调自动验证
        /// 是否gzip压缩
        /// 
        /// 
        public static string HttpPost(string url, CookieContainer cookieContainer = null, Stream postStream = null, Dictionary fileDictionary = null, string refererUrl = null, Encoding encoding = null, X509Certificate2 cer = null, int timeOut = Config.TIME_OUT, bool checkValidationResult = false, bool isGzip = false)
        {
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);
            //是否gzip压缩
            if (isGzip)
            {
                request.Headers["Accept-Encoding"] = "gzip,deflate";
                request.AutomaticDecompression = DecompressionMethods.GZip;
            }
        request.Method = "POST";
        request.Timeout = timeOut;
        request.Proxy = _webproxy;
        if (cer != null)
        {
            request.ClientCertificates.Add(cer);
        }

        if (checkValidationResult)
        {
            ServicePointManager.ServerCertificateValidationCallback =
                new RemoteCertificateValidationCallback(CheckValidationResult);
        }

        #region 处理Form表单文件上传
        var formUploadFile = fileDictionary != null && fileDictionary.Count > 0;//是否用Form上传文件
        if (formUploadFile)
        {
            //通过表单上传文件
            postStream = postStream ?? new MemoryStream();

            string boundary = "----" + DateTime.Now.Ticks.ToString("x");
            //byte[] boundarybytes = Encoding.ASCII.GetBytes("\r\n--" + boundary + "\r\n");
            string fileFormdataTemplate = "\r\n--" + boundary + "\r\nContent-Disposition: form-data; name=\"{0}\"; filename=\"{1}\"\r\nContent-Type: application/octet-stream\r\n\r\n";
            string dataFormdataTemplate = "\r\n--" + boundary +
                                            "\r\nContent-Disposition: form-data; name=\"{0}\"\r\n\r\n{1}";
            foreach (var file in fileDictionary)
            {
                try
                {
                    var fileName = file.Value;
                    //准备文件流
                    using (var fileStream = FileHelper.GetFileStream(fileName))
                    {
                        string formdata = null;
                        if (fileStream != null)
                        {
                            //存在文件
                            formdata = string.Format(fileFormdataTemplate, file.Key, /*fileName*/ Path.GetFileName(fileName));
                        }
                        else
                        {
                            //不存在文件或只是注释
                            formdata = string.Format(dataFormdataTemplate, file.Key, file.Value);
                        }

                        //统一处理
                        var formdataBytes = Encoding.UTF8.GetBytes(postStream.Length == 0 ? formdata.Substring(2, formdata.Length - 2) : formdata);//第一行不需要换行
                        postStream.Write(formdataBytes, 0, formdataBytes.Length);

                        //写入文件
                        if (fileStream != null)
                        {
                            byte[] buffer = new byte[1024];
                            int bytesRead = 0;
                            while ((bytesRead = fileStream.Read(buffer, 0, buffer.Length)) != 0)
                            {
                                postStream.Write(buffer, 0, bytesRead);
                            }
                        }
                    }
                }
                catch (Exception ex)
                {
                    throw ex;
                }
            }
            //结尾
            var footer = Encoding.UTF8.GetBytes("\r\n--" + boundary + "--\r\n");
            postStream.Write(footer, 0, footer.Length);

            request.ContentType = string.Format("multipart/form-data; boundary={0}", boundary);
        }
        else
        {
            request.ContentType = "application/x-www-form-urlencoded";
        }
        #endregion

        request.ContentLength = postStream != null ? postStream.Length : 0;
        request.Accept = "text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8";
        request.KeepAlive = true;

        if (!string.IsNullOrEmpty(refererUrl))
        {
            request.Referer = refererUrl;
        }
        request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/31.0.1650.57 Safari/537.36";

