public static byte[] Serialize(this object o)
{
            if (o == null)
            {
                return null;
            }
            JsonSerializerSettings jsonSerializerSettings = new JsonSerializerSettings(); //应该可以用静态变量
            jsonSerializerSettings.TypeNameHandling = TypeNameHandling.All;   //序列化配置文件,注明需要完整的类型说明
            string json = JsonConvert.SerializeObject(o, jsonSerializerSettings);
            return Encoding.UTF8.GetBytes(json);
}
public static T Deserialize(this byte[] stream)
{
            if (stream == null)
            {
                return default(T);
            }
            JsonSerializerSettings jsonSerializerSettings = new JsonSerializerSettings();
            jsonSerializerSettings.TypeNameHandling = TypeNameHandling.All;
            string json = Encoding.UTF8.GetString(stream);
            return JsonConvert.DeserializeObject(json, jsonSerializerSettings);  //直接从json中读取类型
}
