```
    private void Fetch(LocalMetadataResult item, string metadataFile, XmlReaderSettings settings, CancellationToken cancellationToken)
    {
        if (!SupportsUrlAfterClosingXmlTag)
        {
            using (var streamReader = BaseNfoSaver.GetStreamReader(metadataFile))
            {
                // Use XmlReader for best performance
                using (var reader = XmlReader.Create(streamReader, settings))
                {
                    reader.MoveToContent();
                    // Loop through each element
                    while (reader.Read())
                    {
                        cancellationToken.ThrowIfCancellationRequested();
                    if (reader.NodeType == XmlNodeType.Element)
                    {
                        FetchDataFromXmlNode(reader, item);
                    }
                }
            }
        }
        return;
    }

    using (var streamReader = BaseNfoSaver.GetStreamReader(metadataFile))
    {
        // Need to handle a url after the xml data
        // http://kodi.wiki/view/NFO_files/movies

        var xml = streamReader.ReadToEnd();

        //Find last closing Tag
        var index = xml.LastIndexOf(@"</");

        //If closing tag exists, move to end of Tag
        if (index != -1)
        {
            index = xml.LastIndexOf('>', index);
        }

        if (index != -1)
        {

            var endingXml = xml.Substring(index);

            ParseProviderLinks(item.Item, endingXml);

            // If the file is just an imdb url, don't go any further
            if (index == 0)
            {
                return;
            }

            xml = xml.Substring(0, index + 1);
        }
        else
        {
            // If the file is just an Imdb url, handle that

            ParseProviderLinks(item.Item, xml);

            return;
        }

        using (var ms = new MemoryStream())
        {
            var bytes = Encoding.UTF8.GetBytes(xml);

            ms.Write(bytes, 0, bytes.Length);
            ms.Position = 0;

            // Use XmlReader for best performance
            using (var reader = XmlReader.Create(ms, settings))
            {
                //Catch error reading invalid xml data
                try
                {
                    reader.MoveToContent();

                    // Loop through each element
                    while (reader.Read())
                    {
                        cancellationToken.ThrowIfCancellationRequested();

                        if (reader.NodeType == XmlNodeType.Element)
                        {
                            FetchDataFromXmlNode(reader, item);
                        }
                    }

                }
                catch (XmlException)
                {
                    Logger.Log(LogSeverity.Error, "Error Parsing XML in: " + metadataFile);
                }

            }

        }
    }
}

private void ParseProviderLinks(T item, string xml)
{
    //Split on trailing slash and blank space or new line
    char[] seperators = new char[] { '/', ' ', '\r', '\n' };
    var imdbId = xml.Split(seperators)
        .FirstOrDefault(i => i.StartsWith("tt", StringComparison.OrdinalIgnoreCase));

    if (!string.IsNullOrWhiteSpace(imdbId))
    {
        item.SetProviderId(MetadataProviders.Imdb, imdbId);
    }

    // TODO: Support Tmdb
    // http://www.themoviedb.org/movie/36557
}

```
