@Override
    public boolean dispatchKeyEvent(KeyEvent event) {
        if (event.getKeyCode() == KeyEvent.KEYCODE_BACK) {
            try {
                Log.e("error_Back", "error on key back");
            } catch (ClassCastException ce) {
                Log.e("error_Back", "error on key back");
            }
        }
        return true;
