protected function rename($args) {
        $target = $args['target'];
        $name   = $args['name'];
        $generateNewNameIfExists = $args['generateNewNameIfExists'];
    if (($volume = $this->volume($target)) == false
    ||  ($rm  = $volume->file($target)) == false) {
        return array('error' => $this->error(self::ERROR_RENAME, '#'.$target, self::ERROR_FILE_NOT_FOUND));
    }
    $rm['realpath'] = $volume->realpath($target);

    return ($file = $volume->rename($target, $name, $generateNewNameIfExists)) == false
        ? array('error' => $this->error(self::ERROR_RENAME, $rm['name'], $volume->error()))
        : array('added' => array($file), 'removed' => array($rm));
}

```
elFinderVolumeDriver.class.php:
``` php
public function rename($hash, $name, $generateNewNameIfExists = FALSE) {
        if ($this->commandDisabled('rename')) {
            return $this->setError(elFinder::ERROR_PERM_DENIED);
        }
    if (!$this->nameAccepted($name)) {
        return $this->setError(elFinder::ERROR_INVALID_NAME, $name);
    }

    if (!($file = $this->file($hash))) {
        return $this->setError(elFinder::ERROR_FILE_NOT_FOUND);
    }

    if ($name == $file['name']) {
        return $file;
    }

    if (!empty($file['locked'])) {
        return $this->setError(elFinder::ERROR_LOCKED, $file['name']);
    }

    $path = $this->decode($hash);
    $dir  = $this->_dirname($path);
    $stat = $this->stat($this->_joinPath($dir, $name));

    if ($stat && $generateNewNameIfExists) { // file exists
        $name = $this->uniqueName($dir, $name, '-', false);
    } else if ($stat) {
        return $this->setError(elFinder::ERROR_EXISTS, $name);
    }

    if (!$this->allowCreate($dir, $name)) {
        return $this->setError(elFinder::ERROR_PERM_DENIED);
    }

    $this->rmTmb($file); // remove old name tmbs, we cannot do this after dir move


    if (($path = $this->_move($path, $dir, $name))) {
        $this->clearcache();
        return $this->stat($path);
    }
    return false;
}

