/
 * Simple function to demonstrate how to control file access using "accessControl" callback.
 * This method will disable accessing files/folders starting from  '.' (dot)
 *
 * @param  string  $attr  attribute name (read|write|locked|hidden)
 * @param  string  $path  file path relative to volume root directory started with directory separator
 * @return bool|null
 /
function clear_spec_char($string){
$string = iconv('UTF-8', 'ASCII//TRANSLIT', $string);
$string = strtolower($string);
$string = strtr($string, array('&' => '-', '"' => '-', '&'.'#039;' => '-', '<' => '-', '>' => '-', '\'' => '-'));
$string = preg_replace('/^[^a-z0-9]{0,}(.*?)[^a-z0-9]{0,}$/si', '\1', $string);
$string = preg_replace('/[^a-z0-9.-]/', '-', $string);
$string = preg_replace('/[-]{2,}/', '-', $string);
return $string;
}
function sanitize($cmd, $result, $args, $elfinder) {
$files = $result['added'];

foreach ($files as $file) {
            $name = $file['name'];
            $webalized = clear_spec_char($file['name']);
            if (strcmp($name, $webalized) != 0) {
                $arg = array('target' => $file['hash'], 'name' => $webalized);
                $elfinder->exec('rename', $arg);
            }
        }
return true;
}
function access($attr, $path, $data, $volume) {
    return strpos(basename($path), '.') === 0       // if file/folder begins with '.' (dot)
        ? !($attr == 'read' || $attr == 'write')    // set read+write to false, other (locked+hidden) set to true
        :  null;                                    // else elFinder decide it itself
}
function validName($name) {
    return strpos($name, '.') !== 0;
}
$opts = array(
    // 'debug' => true,
    'bind' => array(
        // '*' => 'logger',
        'mkdir mkfile rename duplicate upload rm paste' => 'sanitize'
    ),
  'roots' => array(
        array(
            'driver'        => 'LocalFileSystem',   // driver for accessing file system (REQUIRED)
            'path'          => $_SERVER['DOCUMENT_ROOT'].'/upload',         // path to files (REQUIRED)
            'utf8fix'    => true,
      'fileMode'     => 0666,         // new files mode
      'dirMode'      => 0777,         // new folders mode
      'tmbPathMode'      => 0777,
      'URL'           => '/upload/', // URL to files (REQUIRED)
      'acceptedName' => 'validName',
            'accessControl' => 'access',             // disable and hide dot starting files (OPTIONAL)
      'attributes' => array(
            array(
