$callback_url = $options['redirecturi'].'?node='.$objurl['node'].'&protocol='.$objurl['protocol'].'&uid='.$userinfo['id'].'&cmd=netmount&host='.$objurl['host'].'&code='.$GET['code'].'&oauth_token='.$tokens.'&user='.$objurl['user'].'&pass=return'.'&='.$objurl['_'];
header("Location: ".$callback_url); 
 exit();
  }
    if ($options['user'] === 'init') {

        if (empty($options['clientId']) || empty($options['clientSecret']) || empty($options['redirecturi'])) {

            return array('exit' => true, 'body' => '{msg:errNetMountNoDriver}');
        }

        try{
            $this->oauth = new Google_Client();

        }catch (Exception $e){

            return array('exit' => true, 'body' => '{msg:errNetMountNoDriver}');
        }

        if ($options['pass'] === 'init') {
            $html = '';

            if (isset($_SESSION['elFinderGoogledriveTokens'])) {
