 */
private function getDBdat($path) {
    $path = $this->getRealpath($path);  
    $path == '/' || $path =='root' ? $itemId= 'root' :  $itemId= basename($path);

    $opts = [         
        'q' => sprintf('trashed=false and "%s" in parents', $itemId)
    ];

    if($res = $this->query($opts,$path)) {          
        return $res[0]; 
    } else {            
        return array();
    }
}

