protected function _filePutContents($path, $content) {
    $res = false;

    if ($local = $this->getTempFile($path)) {
        if (@file_put_contents($local, $content, LOCK_EX) !== false
        && ($fp = @fopen($local, 'rb'))) {
            clearstatcache();
            $res = $this->_save($fp, $path, '', array());
            @fclose($fp);
        }
        file_exists($local) && @unlink($local);
    }

    return $res;
}

