/
 * Return fileinfo 
 *
 * @param  string  $path  file cache
 * @return array
 * @author Dmitry (dio) Levashov
 /
protected function stat($path) {
    if ($path === false || is_null($path)) {
        return false;
    }
    $ret = isset($this->cache[$path])
        ? $this->cache[$path]
        : $this->updateCache($path, $this->convEncOut($this->_stat($this->convEncIn($path))));
    return $ret;
}
/*
 * Root Stat of a volume in session open state
 * @param $path
 * @return array|bool|mixed
 /
protected function rootstat($path){
    $ret = $this->stat($path);// refresh the cache
    $rootKey = md5($path);
    $rs = $this->getRootStatCache();
    if (!$this->isMyReload()) {
        // need $path as key for netmount/netunmount
        if (isset($rs->{$rootKey})) {
            if ($ret = elFinder::sessionDataDecode($rs->{$rootKey}, 'array')) {
                return $ret;
            }
        }
    }
