    $(function(){
        $('body').on('click', function(e){
            var pseudoForm;
            if(e.target.type == 'submit' && (pseudoForm = $(e.target).closest('.pseudo-form')[0])){
                var invalids = $('input:invalid, select:invalid, textarea:invalid', pseudoForm);
                if(invalids.length){
                    invalids.trigger('updatevalidation');
                    e.preventDefault();
                    return false;
                }
            }
        });
    });

```
