export default class CaoHaiYanDatePicker extends React.Component {
static defaultProps = {
    mode: 'datetime',
    value: null,
    //minDate: null,
    //maxDate: null,
    use12Hours: false,
    minuteStep: 1,
    //locale:,
    disabled: false,
    //onChange:,
    //onValueChange:,
    format: modeFormat.datetime,
};

static propTypes = {
    //基础属性
    mode: PropTypes.string,
    value: PropTypes.instanceOf(Date),
    minDate: PropTypes.instanceOf(Date),
    maxDate: PropTypes.instanceOf(Date),
    use12Hours: PropTypes.bool,
    minuteStep: PropTypes.number,
    //locale: PropTypes.object,
    disabled: PropTypes.bool,
    onChange: PropTypes.func,
    onValueChange: PropTypes.func,
    //附加属性
    format:PropTypes.string,
};

constructor(props) {
    super(props);
    this.state = {
        value: null,
        tempValue: null,
    };
}

handleChange = () => {
    //设置内部动态改变值
    let value= this.state.tempValue;
    const onChange = this.props.onChange;
    if (onChange) {
        onChange(value);
    }
    this.setState({ value:value },()=>{
        this.setState({tempValue:null},()=>{
            ActionSheet.close();
        })
    });
}

componentWillReceiveProps(nextProps) {
    //设置外部动态改变值
    this.setState({value: nextProps.value});
}

componentDidMount(){
    //设置默认值
    let value=this.props.value;
    if(value){
        this.setState({value});
        const onChange = this.props.onChange;
        if (onChange) {
            onChange(value);
        }
    }
}

showDatePickerView = () => {
    ActionSheet.showActionSheetWithOptions({
            options: [],
            title: <Flex>
                        <Flex.Item>
                            <a onClick={() => {
                                    this.setState({tempValue:null},()=>{
                                        ActionSheet.close()
                                    })
                               }}
                               style={{color: '#2C9BEC',fontWeight: '500'}}
                            >取消</a>
                        </Flex.Item>
                        <Flex.Item style={{textAlign:'right'}}>
                            <a onClick={this.handleChange}
                               style={{color: '#2C9BEC',fontWeight: '500'}}
                            >确定</a>
                        </Flex.Item>
                     </Flex>,
            message: <DatePickerView
                        style={{width:'100%',height:'100%',borderTop:'1px #ddd solid'}}
                        mode={this.props.mode}
                        minDate={this.props.minDate}
                        maxDate={this.props.maxDate}
                        use12Hours={this.props.use12Hours}
                        minuteStep={this.props.minuteStep}
                        disabled={this.props.disabled}

                        value={this.state.value}
                        onChange={(value) => {
                            this.setState({tempValue:value});
                        }}
                        onValueChange={(...args) => {
                            const onValueChange=this.props.onValueChange;
                            if(onValueChange){
                                onValueChange(args)
                            }
                        }}
                    />,
            maskClosable: true,
        });
}

render() {
