(int32_t)getPhotoWithAsset:(id)asset photoWidth:(CGFloat)photoWidth completion:(void (^)(UIImage photo,NSDictionary info,BOOL isDegraded))completion progressHandler:(void (^)(double progress, NSError error, BOOL stop, NSDictionary info))progressHandler networkAccessAllowed:(BOOL)networkAccessAllowed resizeMode:(PHImageRequestOptionsResizeMode)resizeMode {
    if ([asset isKindOfClass:[PHAsset class]]) {
        CGSize imageSize;
        if (photoWidth < TZScreenWidth && photoWidth < _photoPreviewMaxWidth) {
            imageSize = AssetGridThumbnailSize;
        } else {
            PHAsset phAsset = (PHAsset *)asset;
            CGFloat aspectRatio = phAsset.pixelWidth / (CGFloat)phAsset.pixelHeight;
            CGFloat pixelWidth = photoWidth * TZScreenScale * 1.5;
            // 超宽图片
            if (aspectRatio > 1.8) {
                pixelWidth = pixelWidth * aspectRatio;
            }
//            // 超高图片
            if (aspectRatio < 0.2) {
                pixelWidth = pixelWidth * 0.5;
            }
            CGFloat pixelHeight = pixelWidth / aspectRatio;
            imageSize = CGSizeMake(pixelWidth,pixelHeight);
        }__block UIImage *image;
// 修复获取图片时出现的瞬间内存过高问题
// 下面两行代码，来自hsjcom，他的github是：https://github.com/hsjcom 表示感谢
PHImageRequestOptions *option = [[PHImageRequestOptions alloc] init];
option.resizeMode = resizeMode;
int32_t imageRequestID = [[PHImageManager defaultManager] requestImageForAsset:asset targetSize:imageSize contentMode:PHImageContentModeAspectFill options:option resultHandler:^(UIImage *result, NSDictionary *info) {
    if (result) {
        image = result;
    }
    BOOL downloadFinined = (![[info objectForKey:PHImageCancelledKey] boolValue] && ![info objectForKey:PHImageErrorKey]);
    if (downloadFinined && result) {
        result = [self fixOrientation:result];
        if (completion) completion(result,info,[[info objectForKey:PHImageResultIsDegradedKey] boolValue]);
    }
    // Download image from iCloud / 从iCloud下载图片
    if ([info objectForKey:PHImageResultIsInCloudKey] && !result && networkAccessAllowed) {
        PHImageRequestOptions *options = [[PHImageRequestOptions alloc] init];
        options.progressHandler = ^(double progress, NSError *error, BOOL *stop, NSDictionary *info) {
            dispatch_async(dispatch_get_main_queue(), ^{
                if (progressHandler) {
                    progressHandler(progress, error, stop, info);
                }
            });
        };
        options.networkAccessAllowed = YES;
        options.resizeMode = PHImageRequestOptionsResizeModeFast;
        [[PHImageManager defaultManager] requestImageDataForAsset:asset options:options resultHandler:^(NSData *imageData, NSString *dataUTI, UIImageOrientation orientation, NSDictionary *info) {
            UIImage *resultImage = [UIImage imageWithData:imageData scale:0.1];
            resultImage = [self scaleImage:resultImage toSize:imageSize];
            if (!resultImage) {
                resultImage = image;
            }
            resultImage = [self fixOrientation:resultImage];
            if (completion) completion(resultImage,info,NO);
        }];
    }
}];
return imageRequestID;

} else if ([asset isKindOfClass:[ALAsset class]]) {
    ALAsset alAsset = (ALAsset )asset;
    dispatch_async(dispatch_get_global_queue(0,0), ^{
        CGImageRef thumbnailImageRef = alAsset.thumbnail;
        UIImage *thumbnailImage = [UIImage imageWithCGImage:thumbnailImageRef scale:2.0 orientation:UIImageOrientationUp];
        dispatch_async(dispatch_get_main_queue(), ^{
            if (completion) completion(thumbnailImage,nil,YES);
        if (photoWidth == TZScreenWidth || photoWidth == _photoPreviewMaxWidth) {
            dispatch_async(dispatch_get_global_queue(0,0), ^{
                ALAssetRepresentation *assetRep = [alAsset defaultRepresentation];
                CGImageRef fullScrennImageRef = [assetRep fullScreenImage];
                UIImage *fullScrennImage = [UIImage imageWithCGImage:fullScrennImageRef scale:2.0 orientation:UIImageOrientationUp];

                dispatch_async(dispatch_get_main_queue(), ^{
                    if (completion) completion(fullScrennImage,nil,NO);
                });
            });
        }
    });
});

}
return 0;
}
