js
footer: function(page, pages) { 
    return { 
        columns: [ 
            'Left part of footer',
            { 
                alignment: 'right',
                text: [
                    { text: page.toString(), italics: true },
                    ' of ',
                    { text: pages.toString(), italics: true }
                ]
            }
        ],
        margin: [10, 0]
    };
},
