```
if (!useBlockXOffset && block.height > ctx.availableHeight) {
    var searchValue = page.pageSize.height - page.pageMargins.bottom - ctx.availableHeight;

    // vertical lines
    var changeItems = page.items.filter((item) => {
        return item.item.type ==='line' && item.item.y2 === searchValue;
    });

    changeItems.forEach( (item) => {
        item.item.y2 += ctx.availableHeight;
    });

    // horizontal line
    changeItems = page.items.filter((item) => {
        return item.item.type ==='line' && item.item.y2 === searchValue - item.item.lineWidth / 2;
    });

    changeItems.forEach( (item) => {
        item.item.y1 += ctx.availableHeight;
        item.item.y2 += ctx.availableHeight;
    });

    return false;
}

