```javascript
const Pdfmake = require('pdfmake')
const fs = require('fs')
const fonts = { Roboto: { normal: Buffer.from(require('pdfmake/build/vfs_fonts.js').pdfMake.vfs['Roboto-Regular.ttf'], 'base64') } }
inchToPoint = x => x * 72
noPadding = () => 0
const options = {
    tableLayouts: {
      badgeLayout: {
          paddingLeft: noPadding,
          paddingRight: noPadding,
          paddingTop: noPadding,
          paddingBottom: noPadding,
      }
    }
  }
let docDefinition = {
    pageSize: 'LETTER',
    pageMargins: 0,
    content: [
        {
            layout: 'badgeLayout',
            style: 'badges',
            table: {
                widths: [inchToPoint(3.5)],
                heights: [inchToPoint(2.25)],
                body: [['Badge']]
            }
        }
    ],
    styles: {
        badges: {
            margin: [0, 0, 0, 0]
        }
    },
}
let pdf = (new Pdfmake(fonts)).createPdfKitDocument(docDefinition, options)
pdf.pipe(fs.createWriteStream('sizing_test.pdf'))
pdf.end()
