```java
/*
 * Fix for https://github.com/codecentric/spring-boot-admin/issues/527.
 * Based on {@link FormBodyWrapperFilter}.
 * 
 /
@Slf4j
public class SpringBoot13SupportFilter extends ZuulFilter {
private Field requestField;
private Field servletRequestField;

public SpringBoot13SupportFilter() {
    this.requestField = ReflectionUtils.findField(HttpServletRequestWrapper.class, "req", HttpServletRequest.class);
    this.servletRequestField =
            ReflectionUtils.findField(ServletRequestWrapper.class, "request", ServletRequest.class);
    Assert.notNull(this.requestField, "HttpServletRequestWrapper.req field not found");
    Assert.notNull(this.servletRequestField, "ServletRequestWrapper.request field not found");
    this.requestField.setAccessible(true);
    this.servletRequestField.setAccessible(true);
}

@Override
public String filterType() {
    return "pre";
}

@Override
public int filterOrder() {
    return 1;
}

@Override
public boolean shouldFilter() {
    return true;
}

@Override
public Object run() {
    RequestContext ctx = RequestContext.getCurrentContext();
    HttpServletRequest request = ctx.getRequest();

    log.debug("Filtering {} request to {}", request.getMethod(), request.getRequestURL());

    if (request.getRequestURL().toString().contains("spring-boot-admin/api/applications")
            && request.getMethod().equals("HEAD")) {
        HeadToGetMethodRequestWrapper wrapper = null;
        if (request instanceof HttpServletRequestWrapper) {
            HttpServletRequest wrapped = (HttpServletRequest) ReflectionUtils.getField(this.requestField, request);
            wrapper = new HeadToGetMethodRequestWrapper(wrapped);
            ReflectionUtils.setField(this.requestField, request, wrapper);
            if (request instanceof ServletRequestWrapper) {
                ReflectionUtils.setField(this.servletRequestField, request, wrapper);
            }
        } else {
            wrapper = new HeadToGetMethodRequestWrapper(request);
            ctx.setRequest(wrapper);
        }
    }

    return null;
}

private class HeadToGetMethodRequestWrapper extends HttpServletRequestWrapper {

    private HttpServletRequest request;

    HeadToGetMethodRequestWrapper(HttpServletRequest request) {
        super(request);
        this.request = request;
    }

    @Override
    public HttpServletRequest getRequest() {
        return this.request;
    }

    @Override
    public String getMethod() {
        return "GET";
    }
}

}
