    return new SlackNotifier() {
    @Override
    public void notify(ClientApplicationEvent event) {

        super.notify(event);

        Application application = event.getApplication();
        if (event instanceof ClientApplicationDeregisteredEvent) {
            super.notify(new ClientApplicationStatusChangedEvent(application, StatusInfo.ofDown(), StatusInfo.ofOffline()));
        } else if (event instanceof ClientApplicationRegisteredEvent) {
