                log.debug("Got AWS task info: {}", taskInfoResponse.toString());
                if (taskInfoResponse.getStatusCode() == HttpStatus.OK) {
                    try {
                        final ObjectNode jsonNodes = this.objectMapper.readValue(taskInfoResponse.getBody(), ObjectNode.class);
