var scale = 1.0;
function canvasWheel(params){
    if(zr){
        var storage = zr.storage;
        var els = zr.storage.getDisplayList(true,true);
        var util = require('zrender/core/util');
        var delta = params.wheelDelta ;   
        var newScale = scale + delta/10.0;
