const reg = /({%\s)(?:(require\s+(?:\$id\s=\s)?)('[^']+'|"[^"]+")([\s\S]?%})|(script\s%})([\s\S]+?)({%\sendscript\s*%}))/g;  // eslint-disable-line max-len
