    release: false
  })
  .match('/js/.js', {
      useHash: true,
      release: 'path/$&',
      optimizer: fis.plugin('uglify-js')
  })
  .match('/css/.css', {
      useHash: true,
      release: 'path/$&',
      optimizer: fis.plugin('clean-css')
  })
  .match('/images/.{png,gif,ico,jpg,jpeg}', {
      useHash: true,
      release: 'path/$&',
      optimizer: fis.plugin('png-compressor')
  })
  .match('*.html', {
      release: '/$&'
  })
