```
import java.io.FileNotFoundException;
import java.io.IOException;
import java.io.RandomAccessFile;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.charset.StandardCharsets;
/*
 * Created by Stephen on 6/24/16.
 /
public class DictExtractor {
public static void main(String[] args) {
    try {
        RandomAccessFile dictFile = new RandomAccessFile("/Users/stephen/Downloads/dict", "r");
        FileChannel fc = dictFile.getChannel();
        ByteBuffer lenBuffer = ByteBuffer.allocate(4);
        lenBuffer.clear();
        long endPosition = fc.size();
        long positionToJump = 0x350L; // For each Baidu dict, jump to position 0x350L, the beginning of the tokens.
        fc.position(positionToJump);
        do {
            fc.read(lenBuffer); // lenBuffer is 4 bytes, meaning the length of the token.
            String hexString = bytesToHex(lenBuffer.array());
            int length = little2big(lenBuffer.array()); // Baidu dict format is little-endian, must convert to big-endian.
            if (length == 0) // if length is 0, meaning there is no more token.
                break;
            else {
                System.out.println("HEX: " + hexString); // Print HEX for debugging
                System.out.println("Token length: " + length); // The length of the token string
            }
            lenBuffer.clear();

            fc.position(fc.position() + length * 2); // Move length * 2 offset, this offset is pinyin.

            ByteBuffer tokenBuffer = ByteBuffer.allocate(length * 2);
            fc.read(tokenBuffer);
            byte[] tData = tokenBuffer.array();
            String token = new String(tData, StandardCharsets.UTF_16LE); // Baidu dict encoding is UTF-16 little-endian
            System.out.println("Token: " + token);
        } while (fc.position() < endPosition - 1);
    } catch (FileNotFoundException e) {
        System.err.println("Error: Dict file not found!");
    } catch (IOException e) {
        e.printStackTrace();
    }
}

private static char[] hexArray = "0123456789ABCDEF".toCharArray();

private static String bytesToHex(byte[] bytes) {
    char[] hexChars = new char[bytes.length * 2];
    for (int j = 0; j < bytes.length; j++) {
        int v = bytes[j] & 0xFF;
        hexChars[j * 2] = hexArray[v >>> 4];
        hexChars[j * 2 + 1] = hexArray[v & 0x0F];
    }
    return new String(hexChars);
}

private static int little2big(byte[] b) {
    return ((b[3] & 0xff) << 24) + ((b[2] & 0xff) << 16) + ((b[1] & 0xff) << 8) + (b[0] & 0xff);
}

}
