include 
include 
using namespace std;
define  INF 0x3f3f3f3f
define  maxn 111111
define lson l,m,s<<1
define rson m+1,r,s<<1|1
typedef long long LL;
struct node
{
    int to,next;
};
node edge[maxn << 1];
int head[maxn], tot;
int top[maxn];//top[v]表示v所在的重链的顶端节点
int fa[maxn]; //父亲节点
int dep[maxn];//深度
int sz[maxn];//sz[v]表示以v为根的子树的节点数
int id[maxn];//id[v]表示v与其父亲节点的连边在线段树中的位置
int son[maxn];//重儿子
int pos;
int xorv[maxn<<2];
int num[maxn],a[maxn<<2];
void init()
{
    tot =pos=0;
    memset(head, -1, sizeof(head));
    memset(son, -1, sizeof(son));
}
void add_edge(int u, int v)
{
    edge[tot].to = v;
    edge[tot].next = head[u];
    head[u]=tot++;
}
void dfs_1(int u, int pre, int d)
{
    fa[u] = pre, dep[u] = d, sz[u] = 1;
    for (int i = head[u]; i != -1; i = edge[i].next)
    {
        int v = edge[i].to;
        if (v==pre) continue;
        dfs_1(v, u, d + 1);
        sz[u] += sz[v];
        if (son[u] == -1 || sz[son[u]] < sz[v]) son[u] = v;
    }
}
void dfs_2(int u, int tp)
{
    top[u] = tp;
    id[u] = pos++;
    if (son[u] != -1) dfs_2(son[u], tp);
    for (int i = head[u]; i != -1; i = edge[i].next)
    {
        int v = edge[i].to;
        if (v == fa[u] || v == son[u]) continue;
        dfs_2(v, v);
    }
}
void pushup(int s)
{
    xorv[s] = xorv[s << 1]^xorv[s << 1 | 1];
}
void build(int l, int r, int s)
{
    if (l == r)
    {
        xorv[s]=num[r];
        return;
    }
    int m = (l + r) >> 1;
    build(lson);
    build(rson);
    pushup(s);
}
void update(int p, int v, int l, int r, int s)
{
    if (l == r)
    {
        xorv[s]= v;
        return;
    }
    int m = (l + r) >> 1;
    if (p <= m) update(p, v, lson);
    else update(p, v, rson);
    pushup(s);
}
int query(int ql, int qr, int l, int r, int s)
{
    if (ql <= l&&r <= qr) return xorv[s];
    int m = (l + r) >> 1,ret=0;
    if (ql <= m) ret ^=query(ql, qr, lson);
    if (qr > m) ret ^=query(ql, qr, rson);
    return ret;
}
int get_xor(int u, int v)
{
    int f1 = top[u], f2 = top[v],ret=0;
    while (f1!=f2)
    {
        if (dep[f1] < dep[f2])
        {
            swap(u, v);
            swap(f1, f2);
        }
        ret ^=query(id[f1], id[u], 0, pos - 1, 1);
        u = fa[f1], f1 = top[u];
    }
    if (dep[u] > dep[v])swap(u, v);
    ret ^= query(id[u], id[v], 0, pos - 1, 1);
    return ret;
}
int main()
{
