    init();
    for (int i = 1; i < n; i++)
    {
        int u, v;
        scanf("%d%d", &u, &v);
        add_edge(u, v);
        add_edge(v, u);
    }
    dfs_1(1,0,1);
    dfs_2(1,1);
    for (int i = 1; i <= n; i++)
    {
        int x;
        scanf("%d",&x);
        num[id[i]] = x+1;
    }
    build(0, n-1, 1);
    while(q--)
    {
        int op,x,y;
        scanf("%d%d%d",&op,&x,&y);
        if(op==0)
        {
            update(id[x],y+1,0,n-1,1);
        }
        else
        {
            int ans=get_xor(x,y);
            if(ans==0) printf("-1\n");
            else printf("%d\n",ans-1);
        }
    }
}
return 0;

}
