            files.map((val)=>{
                  if(val.isFile){
            this.meta = [];
            let Fsize=0;
            let modifydate= '';
            val.resolveLocalFilesystemUrl(val.nativeURL).then(
                  (file) =>{
                        file.getMetadata((metadata) => {
                    Fsize= metadata.size; // Get file size
                    modifydate = metadata.modificationTime; 
                    this.meta.push(Fsize);
                    this.meta.push(modifydate);
            }, error => console.log(error))
              }
            );

        this.latestConvertion.unshift({
                name: val.name,
                path : val.nativeURL,
            modifydate: this.meta[1],
            Fsize: this.meta[0]
            });

          }
        })
      }).catch(error=> console.log(JSON.stringify(error)));
  }

