            obj.passport = session.passport;
            obj.lastModified = Date.now();
            obj.cookie = session.cookie.toJSON ? session.cookie.toJSON() : session.cookie;
            obj = JSON.stringify(obj);
            return obj;
        },
        unserialize: function (session) {
            return JSON.parse(session);
        }
