        public class DATATYPE_Reward_ChildArray : DATATYPE_BASE
        {
            public List<UInt32> createFromStreamEx(MemoryStream stream)
            {
                UInt32 size = stream.readUint32();
                List<UInt32> datas = new List<UInt32>();

                while(size > 0)
                {
                    --size;
                    datas.Add(stream.readUint32());
                };

                return datas;
            }

            public void addToStreamEx(Bundle stream, List<UInt32> v)
            {
                stream.writeUint32((UInt32)v.Count);
                for(int i=0; i<v.Count; ++i)
                {
                    stream.writeUint32(v[i]);
                };
            }
        }

        public RewardList createFromStreamEx(MemoryStream stream)
        {
            UInt32 size = stream.readUint32();
            RewardList datas = new RewardList();

            while(size > 0)
            {
                --size;
                datas.Add(itemType.createFromStreamEx(stream));
            };

            return datas;
        }

        public void addToStreamEx(Bundle stream, RewardList v)
        {
            stream.writeUint32((UInt32)v.Count);
            for(int i=0; i<v.Count; ++i)
            {
                itemType.addToStream(stream, v[i]);
            };
        }
    }

    public RewardList createFromStreamEx(MemoryStream stream)
    {
        return itemType.createFromStreamEx(stream);
    }

    public void addToStreamEx(Bundle stream, RewardList v)
    {
        itemType.addToStream(stream, v);
    }
}

