            PyErr_PrintEx(0);
            return false;
        }
        else
        {
            DataType* dataType = (*iter).second->dataType;
            if(!dataType->isSameType(value)){
                return false;
            }
        }

        return true;
    }
}

char err[255];
kbe_snprintf(err, 255, "set FIXED_DICT to a unknown key[%s].\n", keyName);
PyErr_SetString(PyExc_TypeError, err);
PyErr_PrintEx(0);
return false;

}
