   private void initRecycleScroll() {
         recyclerView.addOnScrollListener(new RecyclerView.OnScrollListener() {
            @Override
            public void onScrolled(RecyclerView recyclerView, int dx, int dy) {
                super.onScrolled(recyclerView, dx, dy);
            }
            @Override
            public void onScrollStateChanged(RecyclerView recyclerView, int newState) {
                super.onScrollStateChanged(recyclerView, newState);
                switch (newState) {
                    case RecyclerView.SCROLL_STATE_IDLE:
                        updateProgress(recyclerView);
                        break;
                    case RecyclerView.SCROLL_STATE_DRAGGING:
                        break;
                    case RecyclerView.SCROLL_STATE_SETTLING:
                        break;
                }
            }
        });
    }
    private void updateProgress(RecyclerView view) {
        RecyclerView.LayoutManager layoutManager = view.getLayoutManager();
        int firstVisibleItem = ((LinearLayoutManager) layoutManager).findFirstVisibleItemPosition();
        int lastVisibleItem = ((LinearLayoutManager) layoutManager).findLastVisibleItemPosition();
        int visibleCount = lastVisibleItem - firstVisibleItem;
        JZVideoPlayerStandard jzVideoPlayerStandard;
        for (int i = 0; i < visibleCount; i++) {
            if (layoutManager.getChildAt(i) != null && layoutManager.getChildAt(i).findViewById(R.id.video_player) != null) {
                jzVideoPlayerStandard = layoutManager.getChildAt(i).findViewById(R.id.video_player);
                if (jzVideoPlayerStandard.currentState == JZVideoPlayerStandard.CURRENT_STATE_PLAYING) {
                    jzVideoPlayerStandard.startProgressTimer();
                    break;
                }
            }
        }
    }.

