public class MultipleStateIconicsImageHolder extends ImageHolder {
  private IIcon icon;
  private IIcon pressedIcon;
  private int pressedColor;
public MultipleStateIconicsImageHolder(IIcon icon, int colorDefault, IIcon pressedIcon,
      int pressedColor) {
    this.icon = icon;
    setColor(colorDefault);
    this.pressedIcon = pressedIcon;
    this.pressedColor = pressedColor;
  }
@Override
  protected void apply(ImageView imageView, int colorDefault) {
    IconicsDrawable drawableDefault = getIconicsDrawable(imageView, colorDefault, icon);
IconicsDrawable drawablePressed = getIconicsDrawable(imageView, pressedColor, pressedIcon);

StateListDrawable drawable = new StateListDrawable();

int[][] states = new int[][] {
    new int[] { android.R.attr.state_activated },
    new int[] { android.R.attr.state_checked },
    new int[] { android.R.attr.state_pressed }
};

for (int[] state : states) {
  drawable.addState(state, drawablePressed);
}

drawable.addState(new int[]{}, drawableDefault);

imageView.setImageDrawable(drawable);

}
@NonNull
  private IconicsDrawable getIconicsDrawable(ImageView imageView, int color, IIcon icon) {
    IconicsDrawable drawableDefault = new IconicsDrawable(imageView.getContext());
    drawableDefault.icon(icon);
    drawableDefault.color(color);
    drawableDefault.sizeDp(24);
    drawableDefault.paddingDp(2);
    return drawableDefault;
  }
@Override
  public boolean isValid() {
    return icon != null;
  }
}
```
