    Collection<String> icons = new LinkedList<String>();

    for (Icon value : Icon.values()) {
        icons.add(value.name());
    }

    return icons;
}

@Override
public String getAuthor() {
    return "CLIENT";
}

@Override
public String getUrl() {
    return "http://www.client.com/";
}

@Override
public String getDescription() {
