```
public static class Builder extends DrawerBuilder {
private Activity activity;
private View headerView;

public Builder(@NonNull AppCompatActivity activity) {
    super(activity);
    this.activity = activity;
}

@Override
public DrawerBuilder withHeader(int headerViewRes) {
    LayoutInflater inflater = activity.getLayoutInflater();
    headerView = inflater.inflate(headerViewRes, null, false);

    return this;
}

@Override
public CustomDrawer build() {
    FastAdapter<IDrawerItem> adapter = new FastAdapter<>();
    adapter.withAllowDeselection(false);
    adapter.withSelectable(true);

    withAdapter(adapter);
    super.build();

    CustomDrawer drawer = new CustomDrawer(this);

    if(headerView != null) {
        HeaderItem header = new HeaderItem();
        header.withView(headerView);

        drawer.getHeaderAdapter().add(header);
    }

    RecyclerView rcv = drawer.getRecyclerView();
    rcv.setPadding(rcv.getPaddingLeft(), 0, rcv.getPaddingRight(), rcv.getPaddingBottom());

    mHeaderView = headerView; // Important if you want to find Views in the HeaderView

    SimpleDragCallback touchCallback = new SimpleDragCallback(...);
    ItemTouchHelper touchHelper = new ItemTouchHelper(touchCallback);
    touchHelper.attachToRecyclerView(rcv);

    return drawer;
}

}
