```
private Chain makeChain(T t, String[] fields) {
        NutMap map = NutMap.NEW();
        // 获取数据库字段名称和对象值键值对
        Mirror clazzMirror = Mirror.me(t);// 获取类型的镜像
        for (String field : fields) {
            Field f = null;
            try {
                f = clazzMirror.getField(field);
            } catch (NoSuchFieldException e) {
                e.printStackTrace();
            }
            if (f != null) {
                Column column = f.getAnnotation(Column.class);
                if (!Lang.isEmpty(column)) {
                    if (column.hump()) {
                        String col = org.nutz.lang.Strings.lowerWord(f.getName(), '_');
                        map.addv(col, clazzMirror.getValue(t, f));
                    } else {
                        map.addv(f.getAnnotation(Column.class).value(), clazzMirror.getValue(t, f));
                    }
                }
            }
        }
        return Chain.from(map);
    }
