public void injectValue(Object obj, Record rec, String prefix) {
        try {
            Object val = rec.get((prefix == null ? columnName : prefix + columnName).replaceAll("`", ""));
            this.setValue(obj, val);
        } catch (Exception e) {
            if (log.isTraceEnabled()) {
                log.tracef("columnName=" + columnName, e);
            }
        }
    }
public void injectValue(Object obj, ResultSet rs, String prefix) {
        try {
            this.setValue(obj, adaptor.get(rs, (prefix == null ? columnName : prefix + columnName).replaceAll("`", "")));
        } catch (SQLException e) {
            if (log.isTraceEnabled()) {
                log.tracef("columnName=" + columnName, e);
            }
        }
    }
