net_flush_obufs() {
    rlist_foreach_entry_safe(buffer, batch, in_batch) {
        rlist_add_tail_entry(conn->buffers, buffer, in_batch);
        ev_feed_event(buffer->conn->output, EV_WRITE);
    }
}
iproto_flush(struct iproto_connection conn) {
    struct connection_obuf current_buffer =
        rlist_first_entry(conn->buffers, in_batch);
    struct obuf *buf = current_buffer->obuf;
    flush(buf);
    if (obuf_used(buf) == 0) {
        rlist_del_entry(current_buffer, in_batch);
        rlist_add_entry(connection->empty_buffers, current_buffer,
