struct session_owner {
    enum session_type type;
    int (push)(struct session_owner owner, uint64_t sync,
            struct port *port);
};
struct session {
-   int fd;
-   enum session_type type;
+   struct session_owner *owner;
};
/ iproto.cc /
/
 * After a message notifies IProto thread about pushed data, TX
 * thread can have a new data in one of obufs. This function
 * checks for new pushes and possibly resends push notification
 * to IProto thread.
 /
static void
tx_end_push(struct cmsg msg);
static const struct cmsg_hop push_route[] = {
    { net_send_msg, &tx_pipe },
    { tx_end_push, NULL }
};
struct iproto_push_msg {
    struct cmsg base;
    struct iproto_connection *connection;
};
struct session_owner_binary {
    struct session_owner base;
    bool has_new_pushes;
    struct iproto_push_msg push_msg;
    struct iproto_connection *connection;
};
void
tx_end_push(struct cmsg msg)
{
    struct session_owner_binary session =
        container_of(msg, struct session_owner_binary, push_msg);
    if (session->has_new_pushes) {
        cmsg_init(push_msg, push_route);
        cpipe_push(&net_pipe, push_msg);
        session->has_new_pushes = false;
    }
}
int
iproto_session_push(struct session_owner owner, uint64_t sync,
            struct port port)
{
