spec-custom.js:
``` js
/ A custom reporter based on the built-in spec.js from mocha. The only difference is that it hides messages that start with "Contract: " which truffle unnecessarily adds. /
/*
 * Module dependencies.
 /
var mochaRoot = './node_modules/mocha'
var Base = require(mochaRoot + '/lib/reporters/base');
var inherits = require(mochaRoot + '/lib/utils').inherits;
var color = Base.color;
var cursor = Base.cursor;
function ignoreSuite(suite) {
  return suite.title.slice(0, 10) === 'Contract: ';
}
/*
 * Expose Spec.
 /
exports = module.exports = Spec;
/
 * Initialize a new Spec test reporter.

 * @api public
 * @param {Runner} runner
 /
function Spec(runner) {
  Base.call(this, runner);
var self = this;
  var indents = 0;
  var n = 0;
function indent() {
    return Array(indents).join('  ');
  }
runner.on('start', function() {
    console.log();
  });
runner.on('suite', function(suite) {
    //-- MODIFIED -----------------------------------------------------
    if(!ignoreSuite(suite)) {
      ++indents;
      console.log(color('suite', '%s%s'), indent(), suite.title);
    }
    //-----------------------------------------------------------------
  });
runner.on('suite end', function(suite) {
    //-- MODIFIED -----------------------------------------------------
    if(!suite || !ignoreSuite(suite)) {
      --indents;
      if (indents === 1) {
        console.log();
      }
    }
    //-----------------------------------------------------------------
  });
runner.on('pending', function(test) {
    var fmt = indent() + color('pending', '  - %s');
    console.log(fmt, test.title);
  });
runner.on('pass', function(test) {
    var fmt;
    if (test.speed === 'fast') {
      fmt = indent()
        + color('checkmark', '  ' + Base.symbols.ok)
        + color('pass', ' %s');
      cursor.CR();
      console.log(fmt, test.title);
    } else {
      fmt = indent()
        + color('checkmark', '  ' + Base.symbols.ok)
        + color('pass', ' %s')
        + color(test.speed, ' (%dms)');
      cursor.CR();
      console.log(fmt, test.title, test.duration);
    }
  });
runner.on('fail', function(test) {
    cursor.CR();
    console.log(indent() + color('fail', '  %d) %s'), ++n, test.title);
  });
runner.on('end', self.epilogue.bind(self));
}
/*
 * Inherit from Base.prototype.
 /
inherits(Spec, Base);
```
