``` js
import 'babel-polyfill'
import requireFromString from 'require-from-string'
import { readFileSync } from 'fs'
import { join } from 'path'
import { merge, prop } from 'ramda'
import config from '../../config.js'
import truffleConfig from '../../truffle.js'
const Pudding = require('ether-pudding')
const contractPath = join(__dirname, '../../', config.paths.contracts)
/* Removes the 0x from the beginning of an address /
const stripAddress = addressRaw => addressRaw.slice(0,2) === '0x'
  ? addressRaw.slice(2)
  : address.Raw
/
  Replaces all library placeholders in the given string with addresses provided by a name -> address mapping.
  (source: string, libraries: { name: Address }) => string
/
export const linkSource = (source = '', libraries = {}) => {
  // match library placeholders and capture the identifier
  const regex = /__([^_])_*/g
  return source.replace(regex, (match, name) => {
    if(!(name in libraries)) {
      throw new Error(Found library "${name}" in unlinked bytecode, but no address with that name was provided in libraries mapping.)
    }
    const addressRaw = prop(name)(libraries)
    return stripAddress(addressRaw)
  })
}
/* Compiles all libraries specified in the libraries field of truffle.js. /
export const compileLibraries = async web3 => {
Pudding.setWeb3(web3)
// compile each library
  const libraryNames = truffleConfig.libraries || []
  const libraryInstances = await Promise.all(libraryNames.map(async libName => {
    const Lib = require(join(contractPath, /${libName}.sol.js))
    await Lib.load(Pudding)
    const lib = await Lib.new({ from: web3.eth.accounts[0] })
    return { name: libName, lib }
  }))
// constructor an object mapping: name -> address
  return libraryInstances.reduce((prev, next) => {
    return merge(prev, {
      [next.name]: next.lib.address
    })
  }, {})
}
/* Inserts the given library addresses into the whisked contract code with the given name and returns a final, linked contract constructor. /
export const link = async (web3, name, libraries) => {
  Pudding.setWeb3(web3)
  const puddingContractSource = linkSource(readFileSync(join(contractPath, /${name}.sol.js), 'utf-8'), libraries)
  const Contract = requireFromString(puddingContractSource)
  await Contract.load(Pudding)
  return Contract
}
```
