```C++
bool GetFirstHeapBlock(PRTL_HEAP_INFORMATION curHeapNode, HEAPINFO *hb)
{
  PRTL_HEAP_ENTRY entry = curHeapNode->Entries;
while ((entry->Flags & 2) == 2)
  {
    hb->index++;
    hb->addr = (duint)((unsigned char*)entry->FirstBlock + curHeapNode->EntryOverhead);
    entry = entry + 1;
    hb->size = entry->Size;
  }
// Update the flags
  if ((entry->Flags & 0xF1) != 0 || (entry->Flags & 0x200) != 0)
    hb->flags = 1;
  else if ((entry->Flags & 0x20) != 0)
    hb->flags = 4;
  else if ((entry->Flags & 0x100) != 0)
    hb->flags = 2;
return true;
}
bool GetNextHeapBlock(PRTL_HEAP_INFORMATION curHeapNode, HEAPINFO *hb)
{
  // Make it point to next block address entry
  hb->index++;
  PRTL_HEAP_ENTRY entry = (PRTL_HEAP_ENTRY)(curHeapNode->Entries + hb->index);
  if (entry == NULL)
    return false;
if ((entry->Flags & 2) == 2)
  {
    do
    {
      // New address = current block address + overhead
      hb->addr = (duint)((unsigned char*)entry->FirstBlock + curHeapNode->EntryOverhead);
  // If all the blocks have been enumerated, exit
  if (hb->index > curHeapNode->NumberOfEntries)
    return false;

  hb->index++;
  hb->size = entry->Size;
} while ((entry->Flags & 2) == 2);

}
  else
  {
    // New address = previous address + previous block size
    hb->addr = (duint)(hb->addr + hb->size);
    hb->size = entry->Size;
  }
// Update the flags
  if ((entry->Flags & 0xF1) != 0 || (entry->Flags & 0x200) != 0)
    hb->flags = 1;
  else if ((entry->Flags & 0x20) != 0)
    hb->flags = 4;
  else if ((entry->Flags & 0x100) != 0)
    hb->flags = 2;
return true;
}
bool HandlesEnumHeaps(std::vector & heapList)
{
  typedef PRTL_DEBUG_INFORMATION(NTAPI RTLCREATEDEBUGBUFFER)(ULONG MaximumCommit, BOOLEAN UseEventPair);
  typedef NTSTATUS(NTAPI RTLQUERYPROCESSDEBUGINFORMATION)(HANDLE UniqueProcessId, ULONG Flags, PRTL_DEBUG_INFORMATION Buffer);
  typedef NTSTATUS(NTAPI *RTLDESTROYQUERYDEBUGBUFFER)(PRTL_DEBUG_INFORMATION Buffer);
  static auto RtlCreateQueryDebugBuffer =
    (RTLCREATEDEBUGBUFFER)GetProcAddress(GetModuleHandleW(L"ntdll.dll"), "RtlCreateQueryDebugBuffer");
  static auto RtlQueryProcessDebugInformation =
    (RTLQUERYPROCESSDEBUGINFORMATION)GetProcAddress(GetModuleHandleW(L"ntdll.dll"), "RtlQueryProcessDebugInformation");
  static auto RtlDestroyQueryDebugBuffer =
    (RTLDESTROYQUERYDEBUGBUFFER)GetProcAddress(GetModuleHandleW(L"ntdll.dll"), "RtlDestroyQueryDebugBuffer");
  if (RtlCreateQueryDebugBuffer == NULL || RtlQueryProcessDebugInformation == NULL || RtlDestroyQueryDebugBuffer == NULL)
    return false;
// Create debug buffer
  PRTL_DEBUG_INFORMATION debugInfo = RtlCreateQueryDebugBuffer(0, FALSE);
  if (debugInfo == NULL)
    return false;
// Get process heap data
  NTSTATUS status = RtlQueryProcessDebugInformation((HANDLE)fdProcessInfo->dwProcessId, /PDI_HEAPS | PDI_HEAP_BLOCKS/ 0x14, debugInfo);
  if (status < 0 || debugInfo->Heaps == NULL)
    return false;
// Go through each of the heap nodes
  PRTL_HEAP_INFORMATION heapNodes = debugInfo->Heaps->Heaps;
  HEAPINFO hb;
  for (unsigned int i = 0; i < debugInfo->Heaps->NumberOfHeaps; i++)
  {
    // Enumerate all blocks within this heap node
    memset(&hb, 0, sizeof(hb));
    if (GetFirstHeapBlock(&heapNodes[i], &hb))
    {
      do
      {
        heapList.push_back(hb);
      } while (GetNextHeapBlock(&heapNodes[i], &hb));
    }
  }
RtlDestroyQueryDebugBuffer(debugInfo);
  return true;
}
``
