        ButterKnife.inject(this, view);

        mFeedAdapter = new FeedAdapter(getActivity(), new ArrayList<QuiltPatch>(), mFeedType);
        mFeedAdapter.setOnItemClickListener(FeedFragment.this);
        custom = new QuiltLayout(getActivity());
        mFreeFlowContainer.setLayout(custom);
        mFreeFlowContainer.setAdapter(mFeedAdapter);

        loadData();

        return view;
    }
