Here's the QuiltLayout code

```
public class QuiltLayout extends FreeFlowLayoutBase implements FreeFlowLayout {

    private static final String TAG = "QuiltLayout";

    private static final float REGULAR_HEIGHT_H_RATIO = .4f;
    private static final float LARGE_HEIGHT_H_RATIO = .6f;

    private Context mContext;
    private HashMap<Object, FreeFlowItem> proxies = new HashMap<Object, FreeFlowItem>();
    private Section s;
    private int largeItemSide;
    private int regularItemSide;

    public QuiltLayout(Context context) {
        mContext = context;
    }

    @Override
    public void setDimensions(int measuredWidth, int measuredHeight) {
        super.setDimensions(measuredWidth, measuredHeight);
        regularItemSide = mContext.getResources().getDimensionPixelSize(R.dimen.patch_width_1);
        largeItemSide = mContext.getResources().getDimensionPixelSize(R.dimen.patch_width_2);
    }

    @Override
    public void prepareLayout() {
        proxies.clear();

        s = itemsAdapter.getSection(0);

        for (int i = 0; i < s.getDataCount(); i++) {
            FreeFlowItem freeFlowItem = new FreeFlowItem();
            freeFlowItem.isHeader = false;
            freeFlowItem.itemIndex = i;
            freeFlowItem.itemSection = 0;
            freeFlowItem.data = s.getDataAtIndex(i);

            Rect r = new Rect();

            switch (i) {
                case 0:
                    r.left = 0;
                    r.top = 0;
                    r.right = largeItemSide;
                    r.bottom = height;
                    break;
                case 1:
                    r.left = 2 * regularItemSide;
                    r.top = 0;
                    r.right = r.left + regularItemSide;
                    r.bottom = Math.round(height * REGULAR_HEIGHT_H_RATIO);
                    break;
                case 2:
                    r.left = 2 * regularItemSide;
                    r.top = Math.round(height * REGULAR_HEIGHT_H_RATIO);
                    r.right = r.left + regularItemSide;
                    r.bottom = height;
                    break;
                case 3:
                    r.left = 3 * regularItemSide;
                    r.top = 0;
                    r.right = r.left + regularItemSide;
                    r.bottom = Math.round(height * LARGE_HEIGHT_H_RATIO);
                    break;
                case 4:
                    r.left = 3 * regularItemSide;
                    r.top = Math.round(height * LARGE_HEIGHT_H_RATIO);
                    r.right = r.left + regularItemSide;
                    r.bottom = height;
                    break;
                case 5:
                    r.left = 2 * largeItemSide;
                    r.top = Math.round(height * LARGE_HEIGHT_H_RATIO);
                    r.right = r.left + regularItemSide;
                    r.bottom = height;
                    break;
                default:
                    r.left = i * largeItemSide;
                    r.top = 0;
                    r.right = r.left + regularItemSide;
                    r.bottom = height;
                    break;
            }

            freeFlowItem.frame = r;
            proxies.put(s.getDataAtIndex(i), freeFlowItem);
        }
    }

    @Override
    public HashMap<Object, FreeFlowItem> getItemProxies(int viewPortLeft, int viewPortTop) {
        Rect viewport = new Rect(viewPortLeft, viewPortTop, viewPortLeft + width, viewPortTop + height);
        HashMap<Object, FreeFlowItem> ret = new HashMap<Object, FreeFlowItem>();

        for (Map.Entry<Object, FreeFlowItem> entry : proxies.entrySet()) {
            FreeFlowItem p = entry.getValue();
            if (Rect.intersects(p.frame, viewport)) {
                ret.put(entry.getKey(), p);
            }
        }

        return ret;

    }

    @Override
    public FreeFlowItem getFreeFlowItemForItem(Object item) {
        return proxies.get(item);
    }

    @Override
    public int getContentWidth() {
        if (itemsAdapter == null)
            return 0;

        int sectionIndex = itemsAdapter.getNumberOfSections() - 1;
        Section s = itemsAdapter.getSection(sectionIndex);

        if (s.getDataCount() == 0)
            return 0;

        Object lastFrameData = s.getDataAtIndex(s.getDataCount() - 1);
        FreeFlowItem fd = proxies.get(lastFrameData);

        return fd.frame.left + fd.frame.width();
    }

    @Override
    public int getContentHeight() {
        return height;
    }

    @Override
    public FreeFlowItem getItemAt(float x, float y) {
        return ViewUtils.getItemAt(proxies, (int) x, (int) y);
    }

    @Override
    public boolean verticalScrollEnabled() {
        return false;
    }

    @Override
    public boolean horizontalScrollEnabled() {
        return true;
    }

    @Override
    public void setLayoutParams(FreeFlowLayoutParams params){ }
}
