``` javascript
#!/usr/bin/env node
var fs = require('fs');
var path = require('path');

function compile_jade(src, dst) {
    var jade = require('jade');
    var options = {
        filename: src,
        pretty: true
    };

    try {
        var stat = fs.lstatSync(src);
        if (stat.isFile()) {
            var str = fs.readFileSync(src, 'utf8');
