+  var bd_lng = z * Math.cos(theta) + 0.0065;
+  var bd_lat = z * Math.sin(theta) + 0.006;
+  if (Array.isArray(latlng)) {
+    return [bd_lat, bd_lng];
+  } else {
+    return {'lat': bd_lat, 'lng': bd_lng};
+  }
+}
