+  } else {
+    return {'lat': gg_lat, 'lng': gg_lng};
+  }
+}
+
+function convertLatlng(latlng) {
+  if (isBaiduMap()) return GCJ02toBD09(latlng);
+  return latlng;
+}
+
+function reverseConvertLatlng(latlng) {
