export const STYLE_DEFAULT = 'STYLE_DEFAULT';
export const STYLE_SHORT = 'STYLE_SHORT';
export const STYLE_FULLWIDTH = 'STYLE_FULLWIDTH';

export default React.createClass({

    // Add the Formsy Mixin
    mixins: [Formsy.Mixin],

    propTypes: {
        autofocus: PropTypes.bool,
        className: PropTypes.string,
        defaultValue: PropTypes.bool,
        invalidateEmpty: PropTypes.bool,
        inline: PropTypes.bool,
        label: PropTypes.string,
        labelClass: PropTypes.string,
        name: PropTypes.string.isRequired,
        onBlurCallback: PropTypes.func,
        onChangeCallback: PropTypes.func,
        onFocusCallback: PropTypes.func,
        onResetCallback: PropTypes.func,
        placeholder: PropTypes.string,
        resetButton: PropTypes.bool,
        type: PropTypes.oneOf([
            'color',
            'date',
            'datetime',
            'datetime-local',
            'email',
            'month',
            'number',
            'password',
            'search',
            'tel',
            'text',
            'time',
            'url',
            'week',
        ]),
        value: PropTypes.string,
    },



    getDefaultProps: function() {
        return {
            defaultValue: false,
            invalidateEmpty: false,
            labelClass: 'field__label',
            type: 'text',
        };
    },



    getInitialState: function() {
        return {
            blurTriggered: false,
            defaultValue: this.props.defaultValue,
        };
    },



    componentDidMount: function() {
        if (this.props.autofocus) {
            // when allowed focus this field everytime when component is mounted
            this.didMountTimeout = setTimeout(() => {
                if (this.refs.formField) {
                    this.refs.formField.focus();
                }

                this.didMountTimeout = undefined;
            }, 0);
        }
    },



    componentWillUnmount: function() {
        if (this.didMountTimeout) {
            clearTimeout(this.didMountTimeout);
        }
    },



    // setValue() will set the value of the component, which in
    // turn will validate it and the rest of the form
    _changeValue: function(event) {
        if (this.getErrorMessage() != null) {
            this.setValue(event.currentTarget.value);
        } else {
            if (this.isValidValue(event.target.value)) {
                this.setValue(event.target.value);
            } else {
                this.setState({
                    _value: event.currentTarget.value,
                    _isPristine: false,
                });
            }
        }

        if (this.props.onChangeCallback) {
            this.props.onChangeCallback(event.currentTarget.value);
        }
    },



    _blurValue: function(event) {
        this.setValue(event.currentTarget.value);

        this.setState({
            blurTriggered: true,
        });

        if (this.props.onBlurCallback) {
            this.props.onBlurCallback(event.currentTarget.value);
        }
    },



    _focusField: function() {
        if (this.props.onFocusCallback) {
            this.props.onFocusCallback();
        }
    },



    _resetValue: function() {
        // This workaround has to be there because in some cases we are using
        // <input defaultValue="" /> instead of <input value="" />.
        // And to reset such field we have to use next trick.
        if (this.props.defaultValue) {
            this.refs.formField.value = '';
        } else {
            this.setValue('');
        }

        if (this.props.onResetCallback) {
            this.props.onResetCallback();
        }

        // don't focus field again on touch devices to prevent opening of keyboard
        if (!touchSupported) {
            this.refs.formField.focus();
        }
    },



    _keyDown: function(event) {
        if (event.keyCode == '13') {
            this.setValue(event.currentTarget.value);
        }
    },



    render: function () {
        const {
            inline,
            invalidateEmpty,
            label,
            labelClass,
            name,
            placeholder,
            resetButton,
            style,
            type,
        } = this.props;

        const {
            blurTriggered,
        } = this.state;

        // Set a specific className based on the validation
        // state of this component. showRequired() is true
        // when the value is empty and the required prop is
        // passed to the input. showError() is true when the
        // value typed is invalid
        var componentClasses = classNames({
            'field': true,
            'field--inline': inline,
            'field--required': this.showRequired(),
            'field--invalid': (invalidateEmpty && this.showRequired() && !this.getValue() && blurTriggered) || this.showError(),

            // display valid only when field is required
            'field--valid': this.showRequired() && this.isValid(),
        });

        // An error message is returned ONLY if the component is invalid
        // or the server has returned an error message
        var errorMessage = this.getErrorMessage();

        if (errorMessage) {
            errorMessage = (
                <span className="form-bubble">
                    {errorMessage}
                </span>
            );
        }

        var formLabel;

        if (label) {
            formLabel = (
                <label htmlFor={name} className={labelClass}>{label}</label>
            );
        }

        var resetButtonElement;

        if (resetButton && this.getValue().length > 0) {
            resetButtonElement = (
                <div
                    className="field__reset-btn"
                    onClick={this._resetValue}
                >
                    &times;
                </div>
            );
        }

        var valueParams = {};

        if (this.state.defaultValue) {
            valueParams.defaultValue = this.state._value;
        } else {
            valueParams.value = this.state._value;
        }

        return (
            <div className={componentClasses}>
                {formLabel}
                <div className="field__field-wrapper">
                    <input
                        id={name}
                        className={this.props.className}
                        name={name}
                        onBlur={this._blurValue}
                        onChange={this._changeValue}
                        onFocus={this._focusField}
                        onKeyDown={this._keyDown}
                        placeholder={placeholder}
                        ref="formField"
                        required={this.isRequired()}
                        type={type}
                        {...valueParams}
                    />
                    {resetButtonElement}
                </div>
                {errorMessage}
            </div>
        );
    },

});
```

